import React from 'react';
import { reduxForm, Field } from 'redux-form';
import { Link } from 'react-router-dom';
import RaisedButton from 'material-ui/RaisedButton';
import { TextField } from 'redux-form-material-ui';
import { alphaNumeric, numericOnly, required } from '../../helpers/formValidators';

const style = {
  margin: 12,
};

/**
 * Form component that will be used to add and edit items
 */

export const ItemForm = (props) => {
  const {
    handleSubmit, addingItem, onSubmit, context, editingItem, shId,
  } = props;
  return (
    <form onSubmit={handleSubmit(onSubmit)} className="form">
      <h2>{context} An Item</h2>
      <div>
        <Field
          name="name"
          component={TextField}
          hintText="Name"
          validate={[required, alphaNumeric]}
          floatingLabelText="Name"
        />
      </div>
      <div>
        <Field
          name="quantity"
          component={TextField}
          hintText="Quantity"
          validate={[required, numericOnly]}
          floatingLabelText="Quantity"
        />
      </div>
      <div>
        <RaisedButton label={context} primary style={style} type="Submit" disabled={addingItem} />
        {addingItem && (
          <img
            alt="loading..."
            src="data:image/gif;base64,"
          />
        )}
        {editingItem && (
          <img
            alt="loading..."
            src="data:image/gif;base64,"
          />
        )}
        <Link to={`/shoppinglist/${shId}`} href>
          <RaisedButton
            label="Cancel"
            secondary
            style={style}
            disabled={addingItem || editingItem}
          />
        </Link>
      </div>
    </form>
  );
};

export default reduxForm({ form: 'ITEMFORM' })(ItemForm);
