import React from 'react';
import { reduxForm, Field } from 'redux-form';
import { Link } from 'react-router-dom';
import RaisedButton from 'material-ui/RaisedButton';
import { TextField } from 'redux-form-material-ui';
import { alphaNumeric, required } from '../helpers/formValidators';

const style = {
  margin: 12,
};

const ShoppinglistForm = (props) => {
  const {
    handleSubmit, addingShoppinglist, onSubmit, context, editingShoppinglist
  } = props;
  return (
    <form onSubmit={handleSubmit(onSubmit)} className="form">
      <h2>{context} A Shopping List</h2>
      <div>
        <Field
          name="name"
          component={TextField}
          hintText="Name"
          validate={[required, alphaNumeric]}
          floatingLabelText="Name"
        />
      </div>
      <div>
        <RaisedButton
          label={context}
          primary
          style={style}
          type="Submit"
          disabled={addingShoppinglist}
        />
        {addingShoppinglist && (
          <img
            alt="loading..."
            src="data:image/gif;base64,"
          />
        )}
        {editingShoppinglist && (
          <img
            alt="loading..."
            src="data:image/gif;base64,"
          />
        )}
        <Link to="/dashboard" href>
          <RaisedButton
            label="Cancel"
            secondary
            style={style}
            disabled={props.addingShoppinglist}
          />
        </Link>
      </div>
    </form>
  );
};

export default reduxForm({ form: 'SHOPPINGLISTFORM' })(ShoppinglistForm);
